import os

counter = 0
name = "side_url_suite.py" 

def descargar_proyecto(url):
    global counter 
    counter += 1
    side_content_method = f"""
    def test_scratchdownloader{counter}(self):
        self.driver.get("{url}")
        self.driver.set_window_size(550, 691)
        time.sleep(2)
        self.driver.find_element(By.XPATH, "//img[contains(@src,\'data:image/svg+xml;base64,\')]").click()
        self.driver.find_element(By.CSS_SELECTOR, ".menu_menu-item_3EwYA:nth-child(3)").click()
        time.sleep(2)
    """

    with open(name, 'a') as side_file:  # Abrir en modo 'a' para append
        side_file.write(side_content_method)

# Función para leer las URLs desde un archivo de texto
def leer_urls_desde_archivo(archivo):
    urls = []
    with open(archivo, 'r') as file:
        for line in file:
            urls.append(line.strip())
    return urls

# Nombre del archivo que contiene las URLs
archivo_urls = 'non_exist_projects.txt'

# Obtener las URLs desde el archivo
urls = leer_urls_desde_archivo(archivo_urls)

side_content_header = """
# Generated by Selenium IDE
import pytest
import time
import json
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.support import expected_conditions
from selenium.webdriver.support.wait import WebDriverWait
from selenium.webdriver.common.keys import Keys
from selenium.webdriver.common.desired_capabilities import DesiredCapabilities
from selenium.webdriver.firefox.service import Service


# Configuración de opciones y perfil de Firefox
firefox_options = webdriver.FirefoxOptions()
profile = webdriver.FirefoxProfile()
profile.set_preference("browser.download.folderList", 2)
profile.set_preference("browser.download.dir", "./projects/")
profile.set_preference("browser.helperApps.neverAsk.saveToDisk", "application/octet-stream,application/zip")
firefox_options.profile = profile

# Inicializar el navegador Firefox
service = Service(executable_path='/snap/bin/geckodriver')

class TestScratchdownloader():
    def setup_method(self, method):
        self.driver = webdriver.Firefox(service=service, options=firefox_options)
        self.vars = {}

    def teardown_method(self, method):
        self.driver.quit()
"""

# Escribir el contenido inicial en el archivo solo si está vacío
with open(name, 'w') as side_file:
	side_file.write(side_content_header)

# Descargar cada proyecto desde las URLs obtenidas
for url in urls:
    descargar_proyecto(url)
